import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import {
  initialMessage,
  phaseOnePrompt,
  phaseTwoPrompt,
} from "../data/chatbotPrompts";
import "../styles/Chatbot.css";
import { askChatbot } from "../utils/langchainChatbot"; 
import logo from "../media/logo.png";
import miniLogo from "../media/MH_logo.png";

const Chatbot = () => {
  const [messages, setMessages] = useState([{ sender: "bot", text: initialMessage }]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [phase, setPhase] = useState(1);
  const [chatId, setChatId] = useState(null); // Chat ID for testing
  const [consent, setConsent] = useState(null);

  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  useEffect(() => {
    startNewChat();
  }, []);

  useEffect(() => {
    scrollToBottom();
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, [messages]);

  // Start en ny samtale og få en unik chat ID
  const startNewChat = async () => {
    try {
      const response = await axios.post("http://localhost:5001/saveData/start");
      setChatId(response.data.chatId);
      console.log("Ny samtale startet med ID:", response.data.chatId);
    } catch (error) {
      console.error("❌ Feil ved oppstart av chat:", error);
    }
  };

  // Lagre en melding til backend
  const saveMessage = async (message) => {
    try {
      await axios.post("http://localhost:5001/saveData/save", {
        chatId,
        sender: message.sender,
        text: message.text,
      });
    } catch (error) {
      console.error("❌ Feil ved lagring av melding:", error);
    }
  };

  // Håndtering av samtykke
  const handleConsent = (userConsent) => {
    setConsent(userConsent);
    const userMsg = { sender: "user", text: userConsent ? "Ja, jeg godtar." : "Nei, jeg ønsker ikke lagring." };
    const botMsg = { sender: "bot", text: "Takk for tilbakemeldingen! Da setter vi i gang. Hva heter du?" };

    const newMessages = [...messages, userMsg, botMsg];
    setMessages(newMessages);

    if (userConsent) {
      saveMessage(userMsg);
      saveMessage(botMsg);
    }
  };

  // Send en melding og få svar fra chatboten
  const sendMessage = async () => {
    if (!input.trim()) return;
    setLoading(true);

    const userMessage = { sender: "user", text: input.trim() };
    setMessages((prev) => [...prev, userMessage]);
    saveMessage(userMessage);
    setInput("");

    setIsTyping(true);

    setTimeout(async () => {
      let systemPrompt = phase === 1 ? phaseOnePrompt : phaseTwoPrompt;
      const conversationMessages = messages.map(m => ({ role: m.sender === "bot" ? "assistant" : "user", content: m.text }));

      const botReply = await askChatbot([...conversationMessages, { role: "user", content: input.trim() }], systemPrompt);
      const botMessage = { sender: "bot", text: botReply };

      setMessages((prev) => [...prev, botMessage]);
      saveMessage(botMessage);

      if (phase === 1 && messages.filter(msg => msg.sender === "bot").length >= 5) {
        setPhase(2);
        setMessages((prev) => [...prev, { sender: "bot", text: "La oss gå litt dypere – hva er det egentlig du vil?" }]);
      }

      setIsTyping(false);
      setLoading(false);
    }, 500);
  };

  // Avslutt samtalen og lagre hele chatten som JSON-fil
  const finishChat = async () => {
    try {
      const response = await axios.post("http://localhost:5001/saveData/finish", { chatId });
      console.log("✅ Samtale lagret som JSON-fil:", response.data.filePath);
    } catch (error) {
      console.error("❌ Feil ved lagring av full samtale:", error);
    }
  };

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div className="chat-container">
      <header className="chat-header">
        <img src={logo} alt="MeyerHaugen" className="logo" />
        <p className="chat-date">
          {new Date().toLocaleDateString("no-NO", { weekday: "long", day: "numeric", month: "long" })}
        </p>
      </header>

      <div className="chatbot-messages">
        {messages.map((msg, i) => (
          <div key={i} className={`chat-message ${msg.sender}`}>
            {msg.sender === "bot" && (
              i === messages.length - 1 ? (
                <img src={miniLogo} alt="Bot" className="bot-avatar" />
              ) : (
                <div className="bot-avatar-placeholder"></div>
              )
            )}
            <div className={`chat-bubble ${msg.sender}`}>{msg.text}</div>
          </div>
        ))}

        {isTyping && (
          <div className="typing-bubble">
            <span></span>
            <span></span>
            <span></span>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <p className="chat-id">Chat ID: {chatId}</p>

      {consent === null ? (
        <div className="consent-buttons">
          <button className="accept" onClick={() => handleConsent(true)}>Godta</button>
          <button className="decline" onClick={() => handleConsent(false)}>Avslå</button>
        </div>
      ) : (
        <div className="chat-input">
          <textarea
            ref={inputRef}
            placeholder="Skriv melding her"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter" && !e.shiftKey) {
                e.preventDefault();
                sendMessage();
              }
            }}
            disabled={loading}
            rows={1}
            style={{ resize: "none", minHeight: "30px", maxHeight: "200px", overflowY: "auto" }}
          />
          <button onClick={sendMessage} disabled={loading}>➤</button>
          <button onClick={finishChat} className="finish-chat">Avslutt samtale</button>
        </div>
      )}
    </div>
  );
};

export default Chatbot;
